class Solution {
public:
    int compress(vector<char>& chars) {
        int ind = 0;
        char prev = chars[0];
        int count = 1;
        for(int i = 1;i<chars.size();i++){
            if(chars[i] == prev){
                count++;
            }else{
                chars[ind] = prev;
                ind++;
                if(count > 1){
                    string s = to_string(count);
                    for(auto i : s){
                        chars[ind++] = i;
                    }
                }
                count = 1;
                prev = chars[i];
            }
        }
        chars[ind] = prev;
        ind++;
        if(count > 1){
            string s = to_string(count);
            for(auto i : s){
                chars[ind++] = i;
            }
        }
        return ind;
    }
};
