class Solution {
public:
    bool checkRecord(string s) {
        int a = 0;
        int cnt = 0;
        for(auto i : s){
            if(i == 'L'){
                cnt++;
                if(cnt == 3)return false;
            }else{
                if(i == 'A')a++;
                cnt = 0;
            }
        }
        return a < 2;
    }
};
