int dp[100005][4][3];
class Solution {
public:
    const int m = 1e9 + 7;
    int helper(int n, int l, int a){
        if(n == 0)return 1;
        if(dp[n][l][a] != -1)return dp[n][l][a];
        long long int ans = 0;
        ans = (ans%m + helper(n - 1, 2, a)%m)%m;
        if(a > 0)
            ans = (ans%m + helper(n - 1, 2, a - 1)%m)%m;
        if(l > 0)
            ans = (ans%m + helper(n - 1, l - 1, a)%m)%m;
        return dp[n][l][a] = ans%m;
    }
    int checkRecord(int n) {
        memset(dp, -1, sizeof dp);
        return helper(n, 2, 1)%m;
    }
};
