class Solution {
public:
    int numSubarrayProductLessThanK(vector<int>& nums, int k) {
        if(k <= 1) return 0;

        int i=0;

        int cnt = 0;
        int res = 1;
        for(int j=0;j<nums.size();j++){
            res *= nums[j];
            // cout<<res<<endl;
            while(res >= k) res = res/nums[i++];
            cnt += (j-i+1);

        }

        return cnt;
    }
};
