class Solution {
public:
    int subarraysDivByK(vector<int>& nums, int k) {
        int ans = 0;
        map<int, int> cnt;
        int n = nums.size();

        for(int i = 1; i < n; i++) {
            nums[i] += nums[i - 1];
        }
        for(int i = 0; i < n; i++) {
            nums[i] %= k;
        }
        for(int i = 0; i < n; i++){
            nums[i] = (nums[i] + k) % k;
        }
        for(int i = 0; i < n; i++) {
            ans += cnt[nums[i]];
            cnt[nums[i]]++;
        }
        for(auto i : nums) {
            if(i == 0) {
                ans++;
            }
        }
        return ans;

    }
};
