class Solution {
public:
    set<vector<int>> ans;
    void helper(vector<int> &nums, int ind, vector<int> &arr){
        if(ind == nums.size()){
            ans.insert(arr);
            return;
        };
        helper(nums, ind + 1, arr);
        arr.push_back(nums[ind]);
        helper(nums, ind + 1, arr);
        arr.pop_back();
    }
    vector<vector<int>> subsetsWithDup(vector<int>& nums) {
        ans.clear();
        vector<int> arr;
        sort(nums.begin(), nums.end());
        helper(nums, 0, arr);
        vector<vector<int>> temp;
        for(auto i : ans)
            temp.push_back(i);
        return temp;
    }
};
