class Solution {
public:
    void helper(vector<int> &nums, set<vector<int>> &ans, vector<int> &ds,int ind){
        if(ind == nums.size()){
            ans.insert(ds);
            return;
        }
        helper(nums, ans, ds, ind + 1);
        ds.push_back(nums[ind]);
        helper(nums,ans,ds,ind + 1);
        ds.pop_back();
    }
    vector<vector<int>> subsets(vector<int>& nums) {
        set<vector<int>> st;
        vector<int> ds;
        helper(nums,st,ds,0);
        vector<vector<int>> ans;
        for(auto i : st){
            ans.push_back(i);
        }
        return ans;
    }
};
