class Solution {
public:
    int countGoodSubstrings(string s) {
        int l = 0;
        int r = 2;
        int cnt = 0;
        while(r < s.size()) {
            if(s[l] != s[l+1] and s[l+1] != s[r] and s[l] != s[r]) {
                cnt++;
            }
            l++;
            r++;
        } 
        return cnt;
    }
};
