class Solution {
public:
    int subtractProductAndSum(int n) {
        int temp = n;
        long long prod = 1;
        while(temp > 0){
            prod *= temp%10;
            temp/=10;
        }
        temp = n;
        long long sum = 0;
        while(temp>0){
            sum += temp%10;
            temp/=10;
        }
        return (int)(prod - sum);

    }
};
