class Solution {
public:


    int sumBase(int n, int k) {


        if(k == 10){
            int sum1 = 0;
            while(n){
                sum1 += n%10;
                n/=10;
            }
            return sum1;
        }

        int ans = 0;
        int mul = 1;
        while(n){
            int x = n%k;
            ans = ans + x*mul;
            n/=k;
            mul *= 10;
        }
        int sum = 0;
        while(ans){
            sum += ans%10;
            ans/=10;
        }
        return sum;

    }
};
