class Solution {
public:
    long long matrixSumQueries(int n, vector<vector<int>>& q) {
        long long sum = 0;
        map<int, int> r, c;
        for(int i = q.size() - 1; i >=0; i--){
            if(q[i][0] == 0){
                if(r[q[i][1]] > 0)continue;
                sum -= r[q[i][1]];
                sum += (n - c.size()) * q[i][2];
                r[q[i][1]] ++;
            }else{
                if(c[q[i][1]] > 0)continue;
                sum -= c[q[i][1]];
                sum += (n - r.size()) * q[i][2];
                c[q[i][1]]++;
            }
        }
        return sum;
    }
};
