class Solution {
public:
    int dp[3005][10];
    int find(int num, int k, int p) {
        if(num < 0) {
            return INT_MAX / 2;
        }
        if(num == 0) {
            return 0;
        }
        if(dp[num][k] != -1) return dp[num][k];
        int ans = INT_MAX / 2;
        for(int i = p; i <= num; i += 10) {
            ans = min(ans, 1 + find(num - (i + k), k, i));
        }
        return dp[num][k] = ans;
    }
    int minimumNumbers(int num, int k) {
        if(num == 0) {
            return 0;
        }
        if(k == 0) {
            if(num % 10 == k) {
                return 1;
            }
            return -1;
        }
        memset(dp, -1, sizeof dp);
         int ans = find(num, k, 0);
        if(ans >= INT_MAX / 2) {
            return -1;
        }
        return ans;
    }
};
