class TrieNode{
public:
    map<char, TrieNode *> arr;
    int wordCount;
    TrieNode(){
        wordCount = 0;
    }
};
class Trie{
    TrieNode *root;
  public:
    Trie(){
        root = new TrieNode();
    }
    void add(string &s){
        TrieNode *temp = root;
        for(int i = 0; i < s.length(); i++){
            if(temp->arr[s[i]] == NULL){
                temp->arr[s[i]] = new TrieNode();
            }
            temp = temp->arr[s[i]];
            temp->wordCount++;
        }
    }
    int get(string &s){
        TrieNode *temp = root;
        int res = 0;
        for(int i = 0; i < s.length(); i++){
            temp = temp->arr[s[i]];
            res += temp->wordCount;
        }
        return res;
    }
};
class Solution {
public:
    vector<int> sumPrefixScores(vector<string>& words) {
        Trie *t = new Trie();
        for(auto i : words){
            t->add(i);
        }
        vector<int> ans;
        for(auto i : words){
            string temp;
            ans.push_back(t->get(i));
        }
        return ans;
    }
};
