typedef long long int ll;
class Solution {
public:
    bool judgeSquareSum(int c) {
        for(ll i = 0; i <= sqrt(c);i++){
            ll lo = 0, hi = c;
            while(lo <= hi){
                ll mid = (lo + hi)/2;
                if(i * i + mid * mid == c){
                    return true;
                }else if(i * i + mid * mid > c){
                    hi = mid - 1;
                }else{
                    lo = mid + 1;
                }
            }
        }
        return false;
    }
};
