class Solution {
public:
    int nthSuperUglyNumber(int n, vector<int>& primes) {
        vector<unsigned> dp(n,0);
        vector<unsigned> ptr(n,0);
        unsigned nextMul[primes.size()];
        for(int i=0;i<primes.size();i++){
            nextMul[i]=primes[i];
        }
        dp[0]=1;
        unsigned currElm=INT_MAX;
        for(int i=1;i<n;i++){
            currElm=nextMul[0];
            for(int j=1;j<primes.size();j++){
                if(currElm>nextMul[j])
                    currElm=nextMul[j];
            }
            dp[i]=currElm;
            for(int j=0;j<primes.size();j++){
                if(currElm==nextMul[j]){
                    ptr[j]++;
                    nextMul[j]=dp[ptr[j]]*primes[j];                    
                }
            }
        }
        return dp[n-1];
    }
};
