class Solution {
public:
    long long pickGifts(vector<int>& vec, int k) {
        priority_queue<int> que;
        long long ans=0;
        for(auto i:vec){
            que.push(i);
        }
        while(k--){
            int tp=que.top();
            que.pop();
            que.push(sqrt(tp));

        }
        while(que.size()){
            int tp=que.top();
            que.pop();
            ans+=tp;
        }
        return ans;
    }
};
