class Solution {
public:
    int thirdMax(vector<int>& nums) {
        set<int> st;
        for(auto i : nums)st.insert(i);
        vector<int> arr;
        for(auto i : st)arr.push_back(i);
        if(arr.size() < 3)return arr[arr.size() - 1];
        else return arr[arr.size() - 3];
    }
};
