class Solution {
public:
    int secondsToRemoveOccurrences(string s) {
        int n = s.length();
        int ans = 0;
        while(1){
            bool f =false;
            for(int i = 0; i < n-1;i++){
                if(s[i] == '0' && s[i + 1] == '1'){
                    swap(s[i], s[i + 1]);
                    i++;
                    f = true;
                }
            }
            if(!f)break;
                    ans++;
        }
        return ans;
    }
};
