class Solution {
public:
    vector<int> topKFrequent(vector<int>& nums, int k) {
        map<int, int> mp;
        for(auto i : nums)mp[i]++;
        vector<int> ans;
        priority_queue<pair<int, int>> pq;
        for(auto i : mp)pq.push({i.second, i.first});

        for(int i = 0; i < k && !pq.empty(); i++, pq.pop()){
            auto f = pq.top();
            ans.push_back(f.second);
        }
        return ans;
    }
};
