bool cmpr(pair<int, string> &a, pair<int, string> &b){
    if(a.first > b.first){
        return true;
    }else if(a.first < b.first){
        return false;
    }else{
        return lexicographical_compare(a.second.begin(), a.second.end(), b.second.begin(),  b.second.end());
    }
}
class Solution {
public:

    vector<string> topKFrequent(vector<string>& words, int k) {
        unordered_map<string, int> umap;
        for(auto i : words){
            umap[i]  ++;
        }
        vector<pair<int, string>> arr;
        for(auto i : umap){
            arr.push_back(make_pair(i.second, i.first));
        }
        sort(arr.begin(), arr.end(), cmpr);
        vector<string> ans;
        for(int i = 0; i < ( k);i++){
            ans.push_back(arr[i].second);
        }
        return ans;
    }
};
