class Solution {
public:
    int totalHammingDistance(vector<int>& nums) {
        int n = nums.size();
        int ans = 0;
       for(int i=30;i>=0;i--) {
           int cnt0 = 0;
           int cnt1  = 0;
           for(int j=0;j<n;j++) {
               if(nums[j] & (1<<i)) cnt1++;
               else cnt0++;
           }
           ans += (cnt1 * cnt0);
       }
        return ans;

    }
};
