class Solution
{
public:
    int trap(vector<int> &arr)
    {
        int n = arr.size();
        vector<int> left(n, 0), right(n, 0);
        int _max = INT_MIN;
        for (int i = 0; i < n; i++)
        {
            if (_max < arr[i])
            {
                _max = arr[i];
            }
            left[i] = _max;
        }
        _max = INT_MIN;
        for (int i = n - 1; i >= 0; i--)
        {
            if (_max < arr[i])
            {
                _max = arr[i];
            }
            right[i] = _max;
        }

        int ans = 0;
        for (int i = 0; i < n; i++)
        {
            ans += min(left[i], right[i]) - arr[i];
        }
        return ans;
    }
};
