class Solution {
public:
    int dp[205][205];
    int helper(vector<vector<int>>& triangle, int row, int ind){
        if(row == triangle.size())return 0;
        if(dp[row][ind] != -1)return dp[row][ind];
        return dp[row][ind] = min(helper(triangle, row + 1, ind), helper(triangle, row + 1, ind + 1)) + triangle[row][ind];
    }
    int minimumTotal(vector<vector<int>>& triangle) {
        memset(dp, -1, sizeof dp);
        return helper(triangle, 0, 0);
    }
};
