class Solution {
public:
    int twoCitySchedCost(vector<vector<int>>& costs) {
        int n = costs.size();

        int sum = 0;
        vector<int> t;
        for(auto i : costs) {
            sum += i[0];
            t.push_back(i[1] - i[0]);
        }
        sort(t.begin(), t.end());

        for(int i  =0; i < n / 2; i++) {
            sum += t[i];
        }
        return sum;
    }
};
