class Solution {
public:
    vector<int> twoSum(vector<int>& arr, int target) {
        unordered_map<int, int> umap;
        for(int i = 0; i < arr.size();i++){
            if(umap[target - arr[i]] > 0){
                return {umap[target - arr[i]] - 1, i};
            }
            umap[arr[i]] = i + 1;
        }
        return {};
    }
};
