#define ll long long
class Solution {
public:

    ll cnt(ll n, ll a, ll b, ll c) {

        ll ab = (a*b)/__gcd(a, b);
        ll bc = (b*c)/__gcd(b, c);
        ll ac = (a*c)/__gcd(a, c);
        ll abc = (a*bc)/__gcd(a, bc);
        ll ans = n/a + n/b + n/c - n/ab - n/bc - n/ac + n/abc;
        return ans;

    }
    ll nthUglyNumber(ll n, ll a, ll b, ll c) {
       ll s = min({a, b, c});
        ll e = n*s;
        while(s < e) {
            ll mid = s + (e-s)/2;
            if(cnt(mid, a, b, c) < n) {
                s = mid + 1;
            }
            else {
                e = mid;
            }

        }

        return s;
    }
};
