class Solution {
public:
    bool prime(int n){
        for(int i = 2;i<= sqrt(n);i++)
            if(n % i == 0)return false;

        return true;
    }
    bool isUgly(int num) {
        for (int i=2; i<6 && num; i++)
            while (num % i == 0)
                num /= i;
        return num == 1;
    }
};
