class Solution {
public:
    vector<string> uncommonFromSentences(string A, string B) {
        vector<string> v;
        unordered_map<string,int> h;
        for(int i=0;i<A.size();i++){

            string ans = "";
            while(i != A.size() and A[i] != ' '){
                ans += A[i];
                i++;
            }
            h[ans]++;

        }

        for(int i=0;i<B.size();i++){

            string ans = "";
            while(i != B.size() and B[i] != ' '){
                ans += B[i];
                i++;
            }
            h[ans]++;

        }


        for(auto node:h){
            if(node.second == 1){
                v.push_back(node.first);
            }
        }
        return v;
    }
};
