class Solution {
public:
    int numUniqueEmails(vector<string>& emails) {
      set<string> st;

    for (int i = 0; i < emails.size(); i++) {
        string s = emails[i];
        string s1 = "";
        int j  = 0;
        for (j = 0; s[j] != '@'; j++) {
            if (s[j] == '.') continue;
            else if (s[j] == '+') break;
            else s1 += s[j];
        }
        for (int k = s.size() - 1 ; s[k] != '@'; k--) {
            // if (s[k] == '.') s1 += s[k];
            // else
            // if()
            s1 += s[k];
        }
        st.insert(s1);
        // cout << s1 << endl;

    }


    // cout << st.size();

        return st.size();
    }
};
