class Solution {
public:
    int uniqueMorseRepresentations(vector<string>& words) {
        vector<string> code =  {".-","-...","-.-.","-..",".","..-.","--.","....","..",".---","-.-",".-..","--","-.","---",".--.","--.-",".-.","...","-","..-","...-",".--","-..-","-.--","--.."};
        unordered_map<string, int> umap;

        for(auto i : words){
            string temp = "";
            for(auto j : i){
                temp.append(code[j-'a']);
            }
            umap[temp] = 1;
        }
        return umap.size();
    }
};
