int dp[105][105];
class Solution {
public:
    int helper(int m, int n, int x,int y){
        if(x >= m || y >= n){
            return 0;
        }
        if(x == m - 1 && y == n - 1){
            return 1;
        }
        if(dp[x][y] != -1)return dp[x][y];
        return dp[x][y] = helper(m,n,x + 1, y) + helper(m, n, x, y + 1);
    }
    int uniquePaths(int m, int n) {
        memset(dp, -1, sizeof dp);
        return helper(m,n,0,0);        
    }
};
