class Solution {
public:
    bool validMountainArray(vector<int>& arr) {
      if(arr.size()<3) return false;
        int count=0;
        int idx=-1;
        for(int i=1;i<=arr.size()-2;i++){
            if(arr[i]>arr[i-1]&&arr[i]>arr[i+1]){
                idx=i;
                break;
            }
        }
        if(idx==-1) return false;
        cout<<idx<<" ";
        for(int i=0;i<idx;i++){
            if(arr[i]>=arr[i+1]) return false;
        }
        for(int i=arr.size()-1;i>idx;i--){
            if(arr[i]>=arr[i-1]) return false;
        }

        return true;
    }
};
