class Solution {
public:
    bool helper(string &s, int i, int j, int k){
        if(k < 0)return false;
        if(i >= j)return true;
        if(s[i] == s[j]){
            return helper(s, i + 1, j - 1, k);
        }else{
            return helper(s, i + 1, j, k - 1) || helper(s, i, j - 1, k-1);
        }
    }
    bool validPalindrome(string s) {
        return helper(s, 0, s.length() - 1, 1);
    }
};
