class Solution {
public:
    bool isOpen(char c)
    {
        vector<char> open{'(', '{', '['};
        if (open[0] == c || open[1] == c || open[2] == c)
        {
            return true;
        }
        else
        {
            return false;
        }
    }
    bool isSameGroup(char c, char d)
    {
        if (c == '{' && d == '}')
        {
            return true;
        }

        if (c == '(' && d == ')')
        {
            return true;
        }

        if (c == '[' && d == ']')
        {
            return true;
        }
        return false;
    }
    bool isValid(string s)
    {
        stack<char> st;
        for (char i : s)
        {
            if (!isOpen(i))
            {
                if (st.empty())
                {
                    return false;
                }
                else
                {
                    if (isSameGroup(st.top(), i))
                    {
                        st.pop();
                    }
                    else{
                        return false;
                    }
                }
            }
            else
            {
                st.push(i);
            }
        }
        if (st.empty())
        {
            return true;
        }
        else
        {
            return false;
        }
    }

};
