class Solution {
public:
    int triangleNumber(vector<int>& nums) {
        sort(nums.begin(), nums.end());
        int ans = 0;
        int n= nums.size();
        for(int i = 0; i < n;i++){
            for(int j = i + 1; j < n;j++){
                int sum = nums[i] + nums[j];
                int ind = lower_bound(nums.begin(), nums.end(), sum) - nums.begin();
                ind--;
                if(ind > j){
                    ans += (ind - j);
                }
            }
        }
        return ans;
    }
};
