int dp[1005][1005];
class Solution {
public:
    int helper(stack<int> &st, vector<int> &pushed, vector<int>&popped, int ind1, int ind2){
        if(ind1 == pushed.size() && ind2 == popped.size())return true;
        if(dp[ind1][ind2] != -1)return dp[ind1][ind2];
        bool f = false;
        if(ind1 < pushed.size()){
            st.push(pushed[ind1]);
            f |= helper(st, pushed, popped, ind1 + 1, ind2);
            st.pop();
        }
        if(ind2 < popped.size() && !st.empty() &&st.top() == popped[ind2]){
            int t = st.top();
            st.pop();
            f |= helper(st, pushed, popped, ind1, ind2 + 1);
            st.push(t);
        }
        return dp[ind1][ind2] = f;
    }
    bool validateStackSequences(vector<int>& pushed, vector<int>& popped) {
        stack<int> st;
        memset(dp, -1, sizeof dp);
        return helper(st, pushed, popped, 0, 0);
    }
};
