class Solution {
public:
    bool canMeasureWater(int jug1, int jug2, int target) {
        int x = jug1,y = jug2,z=x+y;
        int step[]={x,-x,-y,y};
        queue<int> q;
        vector<int> vis(z+1,0);
        q.push(0);
        vis[0]=1;
        while(!q.empty()){
            int node = q.front();
            q.pop();
            if(node==target){
                return true;
            }
            for(int i =0;i<4;i++){
                int newnode = node+step[i];
                if(newnode>=0 && newnode<=z && vis[newnode]==0){
                    q.push(newnode);
                    vis[newnode]=1;
                }
            }
        }
        return false;
    }
};
