int dp[2005];
class Solution {
public:

    bool helper(string &s, int ind, unordered_map<string, int> &wordDict){
        if(ind == s.length()){
            return true;
        }
        if(dp[ind] != -1) return dp[ind];
        string temp = "";
        for(int i = ind; i < s.length(); i ++){
            temp.push_back(s[i]);
            if(wordDict.find(temp) != wordDict.end()){
                if(helper(s, i + 1, wordDict)){
                    return dp[ind] = true;
                }
            }
        }
        return dp[ind] = false;
    }
    bool wordBreak(string s, vector<string>& wordDict) {
        memset(dp, -1, sizeof dp);
        unordered_map<string, int> map;
        for(auto i : wordDict){
            map[i]++;
        }
        return helper(s, 0, map);        
    }
};
