class Solution {
public:
    vector<string> wordSubsets(vector<string>& words1, vector<string>& words2) {
        vector<int> freq(26, 0);
        for(auto i : words2){
            vector<int> temp(26,0);
            for(auto j : i){
                temp[j - 'a']++;
                freq[j-'a'] = max(freq[j-'a'], temp[j - 'a']);
            }
        }
        vector<string> ans;
        for(auto i : words1){
            vector<int> temp(26,0);
            for(auto j : i){
                temp[j-'a']++;
            }
            int f = 1;
            for(int i = 0;i<26;i++){
                if(temp[i] < freq[i]){
                    f = 0;
                    break;
                }
            }
            if(f){
                ans.push_back(i);
            }
        }
        return ans;        
    }
};
