class Solution {
public:
    vector<string> twoEditWords(vector<string>& queries, vector<string>& dictionary) {
        vector<string> ans;
        for(auto q : queries){
            for(auto s : dictionary){
                int c = 0;
                for(int i = 0; i < s.length(); i++){
                    if(s[i] != q[i])c++;
                }
                if(c <= 2){
                    ans.push_back(q);
                        break;
                }
            }
        }
        return ans;
    }
};
