class Solution {
public:
    vector<int> xorQueries(vector<int>& arr, vector<vector<int>>& queries) {
        vector<int> pre = arr;
        for(int i = 1; i < arr.size();i++){
            pre[i] = (pre[i-1] ^ pre[i]);
        }
        vector<int> ans;
        for(auto i : queries){
            if(i[0] == 0){
                ans.push_back(pre[i[1]]);
            }else{
                ans.push_back((pre[i[0]-1] ^ pre[i[1]]));
            }
        }
        return ans;
    }
};
