class Solution {
public:
    string convert(string s, int numRows)
    {
        vector<char> temp;
        vector<vector<char>> arr(numRows, temp);
        int k = numRows - 2;
        int i = 0;
        bool isNow = false;

        while (i != s.length())
        {
            if (isNow)
            {
                while (k > 0 && i < s.length())
                {
                    arr[k].push_back(s[i]);
                    i++;
                    k--;
                }
                k = numRows - 2;
                isNow = false;
            }
            else
            {
                for (int j = 0; j < numRows && i < s.length(); j++, i++)
                {
                    arr[j].push_back(s[i]);
                }
                isNow = true;
            }
        }
        string s1;
        for (auto temp1 : arr)
        {
            for (auto temp2 : temp1)
            {
                s1.push_back(temp2);
            }
        }
        return s1;
    }
};
